import { Vector } from "ol/layer";
import VectorSource from "ol/source/Vector.js";
import { Stroke, Style, Icon } from "ol/style";
import { and, equalTo } from "ol/format/filter";
import { WFS, GeoJSON } from "ol/format";
import Feature from "ol/Feature.js";
import Point from "ol/geom/Point.js";
import { fromLonLat } from "ol/proj.js";

const map = $("#map").data("map");
const searchBtn = $("#search");

const wfsUrl = "http://localhost:8888/geoserver/Training/wfs";

const rome = new Feature({
  geometry: new Point(fromLonLat([168506.07514114826, 154845.75917416273])),
});

rome.setStyle(
  new Style({
    image: new Icon({
      color: "#BADA55",
      crossOrigin: "anonymous",
      src: "https://e7.pngegg.com/pngimages/409/413/png-clipart-map-drawing-pin-map-marker-angle-heart-thumbnail.png",
    }),
  })
);

const vectorSource = new VectorSource({
  features: [rome],
});

const style = new Style({
  stroke: new Stroke({
    color: "blue",
    width: 5,
  }),
});

const vector = new Vector({
  source: vectorSource,
  style: style,
});

map.addLayer(vector);

searchBtn.click(function () {
  const parcel = $("#parcelInput").val().toString();
  const block = $("#blockInput").val().toString();

  if (parcel.length == 0) {
    window.alert("Please enter parcel number.");
    return;
  }
  // if (block.length == 0) {
  //   window.alert("Please enter block number.");
  //   return;
  // }

  const featureRequest = new WFS().writeGetFeature({
    srcName: "EPSG:28191",
    featureNS: "http://localhost:8888/geoserver/Training",
    featurePrefix: "Parcels",
    featureTypes: ["Parcels"],
    outputFormat: "application/json",
    filter: and(equalTo("parcel_n", parcel), equalTo("block_n", block)),
    // filter: equalTo("parcel_n", parcel),
  });

  fetch(wfsUrl, {
    method: "POST",
    body: new XMLSerializer().serializeToString(featureRequest),
  })
    .then(function (response) {
      return response.json();
    })
    .then(function (json) {
      if (json.features.length > 0) {
        const layersDiv = $("#draggable");
        const layersDivContent = $("#draggable-content");
        const layersDivTitle = $("#draggable-title");
        layersDivTitle.html("List search");
        layersDivContent.html("");
        layersDiv.css("display", "block");

        json.features.forEach((layer) => {
          const element = `<div class="list-search">
              <p>
              parcel_n: ${layer.properties.parcel_n}
              </p>
              <p>
              block_n: ${layer.properties.block_n}
              </p>
              <a href="#" class="zoom">Xem</a>
            </div>`;

          const $element = $(element);
          const $zoom = $element.find(".zoom");

          layersDivContent.append($element);

          $zoom.click(function (e) {
            const features = new GeoJSON().readFeatures(layer);
            vectorSource.clear(true);
            vectorSource.addFeatures(features);
            // const coordinate = e.coordinate;
            var pixel = map.getEventPixel(e.originalEvent);
            var coordinate = map.getCoordinateFromPixel(pixel);
            console.log(coordinate);
            map
              .getView()
              .fit(vectorSource.getExtent(), { padding: [100, 100, 100, 100] });

            // var markers = new ol.layer.Vector({
            //   source: new ol.source.Vector(),
            //   style: new ol.style.Style({
            //     image: new ol.style.Icon({
            //       anchor: [0.5, 1],
            //       src: "https://e7.pngegg.com/pngimages/409/413/png-clipart-map-drawing-pin-map-marker-angle-heart-thumbnail.png",
            //     }),
            //   }),
            // });
            // map.addLayer(markers);
            // var marker = new ol.Feature(new ol.geom.Point(coordinate));
            // markers.getSource().addFeature(marker);
          });
        });
      } else {
        const layersDiv = $("#draggable");
        layersDiv.css("display", "none");
        window.alert("No features found.");
      }
    });
});
